import React, { useEffect, useState, useRef } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Toast } from "primereact/toast";

import 'primereact/resources/themes/bootstrap4-light-purple/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const toast = useRef(null);

  useEffect(() => {
    fetch("http://localhost:5000/leaderboard")
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched data:", data); // Veriyi konsola yazdırıyoruz
        setPlayers(data);
      })
      .catch((err) => {
        console.error("Fetch hatası:", err);
        toast.current.show({
          severity: "error",
          summary: "Hata",
          detail: "Veriler alınırken bir hata oluştu.",
        });
      });
  }, []);

  const moneyBodyTemplate = (rowData) => {
    return `$${rowData.money.toLocaleString()}`;
  };

  return (
    <div>
      <h1>Leaderboard</h1>
      <Toast ref={toast} />
      <DataTable value={players} paginator rows={10} responsiveLayout="scroll">
        <Column field="country" header="Country" sortable />
        <Column field="name" header="Username" sortable />
        <Column header="Rank" body={(rowData, { rowIndex }) => rowIndex + 1} />
        <Column field="money" header="Money" body={moneyBodyTemplate} sortable />
      </DataTable>
    </div>
  );
};

export default Leaderboard;
