import React, { useEffect, useState, useRef } from "react";
import { Toast } from "primereact/toast";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { InputText } from "primereact/inputtext";
import { Dropdown } from "primereact/dropdown";
import { AutoComplete } from "primereact/autocomplete"; // ✅ AutoComplete ekledik
import "./App.css";
import "./index.css";

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedCountry, setSelectedCountry] = useState("All");
  const [suggestions, setSuggestions] = useState([]); // ✅ AutoComplete için öneriler
  const toast = useRef(null);

  useEffect(() => {
    fetch("http://localhost:5000/leaderboard")
      .then((res) => res.json())
      .then((data) => {
        setPlayers(data);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        toast.current.show({
          severity: "error",
          summary: "Error",
          detail: "An error occurred while fetching data.",
        });
      });
  }, []);

  const countries = [
    "All",
    ...new Set(players.map((player) => player.country)),
  ];

  // Kullanıcı sadece yazdığında tabloyu güncelleme (Sadece önerileri gösterir)
  const onSearchChange = (e) => {
    setSearch(e.value);
  };

  // Kullanıcı bir oyuncuya tıklarsa, tabloyu sadece o oyuncuya göre filtrele
  const onSearchSelect = (e) => {
    setSearch(e.value);
    setSelectedCountry("All"); // Ülke filtresini sıfırla
  };

  const onCountryChange = (e) => {
    setSelectedCountry(e.value);
  };

  // AutoComplete için ülke filtresini de dikkate al
  const onSearchInput = (event) => {
    const query = event.query.toLowerCase();
    const filteredPlayers = players.filter(
      (player) =>
        player.name.toLowerCase().includes(query) &&
        (selectedCountry === "All" || player.country === selectedCountry) // Ülke filtresini uygula
    );

    const filteredSuggestions = filteredPlayers.map((player) => player.name);
    setSuggestions(filteredSuggestions);
  };

  const filteredPlayers = players.filter(
    (player) =>
      player.name.toLowerCase().includes(search.toLowerCase()) &&
      (selectedCountry === "All" || player.country === selectedCountry)
  );

  return (
    <div className="container">
      <div className="leaderboard-header">
        <h1>Leaderboard</h1>
      </div>
  
      <div className="filters">
  <AutoComplete
    value={search}
    suggestions={suggestions}
    completeMethod={onSearchInput}
    onChange={onSearchChange}
    onSelect={onSearchSelect}
    placeholder="Search player..."
    className="p-autocomplete"
  />

  <Dropdown
    value={selectedCountry}
    options={countries.map((country) => ({ label: country, value: country }))}
    onChange={onCountryChange}
    placeholder="Filter by Country"
    className="p-dropdown"
  />
</div>

  
      <div className="table-container">
        <DataTable 
        value={filteredPlayers} 
        paginator 
        rows={10} 
        reorderableColumns 
        responsiveLayout="scroll"
        footer="Footer içerik" style={{ '--datatable-footer-bg': '#4CAF50' }}
        >
          <Column field="name" header="Player Name" sortable reorderable />
          <Column field="country" header="Country" sortable reorderable />
          <Column field="money" header="Money" sortable reorderable />
        </DataTable>
      </div>
  
      <Toast ref={toast} />
    </div>
  );
  
  
};

export default Leaderboard;
