import React, { useEffect, useState } from "react";

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("All");

  useEffect(() => {
    fetch("http://localhost:5000/leaderboard")
      .then((response) => response.json())
      .then((data) => {
        console.log("Gelen veri:", data);  // ✅ API’den gelen veriyi gör
        setPlayers(data);
        setFilteredPlayers(data); // Başlangıçta tüm oyuncuları göster
      })
      .catch((error) => console.error("Error fetching leaderboard:", error));
  }, []);





  // Ülkeye göre filtreleme fonksiyonu
  const handleCountryChange = (event) => {
    const country = event.target.value;
    setSelectedCountry(country);

    if (country === "All") {
      setFilteredPlayers(players); // Tüm oyuncuları göster
    } else {
      setFilteredPlayers(players.filter((player) => player.country === country));
    }
  };

  // Tüm ülkeleri listele (tekrarlananları kaldır)
  const uniqueCountries = ["All", ...new Set(players.map((player) => player.country))];

  return (
    <div>
      <h1>Leaderboard</h1>

      {/* Ülke seçme dropdown */}
      <label>Filter by Country: </label>
      <select value={selectedCountry} onChange={handleCountryChange}>
        {uniqueCountries.map((country) => (
          <option key={country} value={country}>
            {country}
          </option>
        ))}
      </select>

      <table>
        <thead>
          <tr>
            <th>Country</th>
            <th>Username</th>
            <th>Rank</th>
            <th>Money</th>
          </tr>
        </thead>
        <tbody>
          {filteredPlayers.map((player, index) => (
            <tr key={player.playerId}>
              <td>{player.country}</td>
              <td>{player.name}</td>
              <td>{index + 1}</td>
              <td>${player.money}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
