import { useState, useEffect } from "react";

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("All");

  useEffect(() => {
    fetch("http://localhost:5000/leaderboard")
      .then((res) => res.json())
      .then((data) => {
        console.log("Gelen veri:", data); // API verisini kontrol et
        setPlayers(data);
        const uniqueCountries = ["All", ...new Set(data.map(player => player.country))];
        console.log("Ülkeler:", uniqueCountries); // Dropdown listesi oluşuyor mu?
        setCountries(uniqueCountries);
      })
      .catch((err) => console.error("Error fetching leaderboard:", err));
  }, []);
  

  const filteredPlayers = selectedCountry === "All"
    ? players
    : players.filter(player => player.country === selectedCountry);

  return (
    <div>
      <h1>Leaderboard</h1>
      <label>Filter by Country: </label>
      <select onChange={(e) => setSelectedCountry(e.target.value)} value={selectedCountry}>
        {countries.map((country, index) => (
          <option key={index} value={country}>{country}</option>
        ))}
      </select>

      <table>
        <thead>
          <tr>
            <th>Country</th>
            <th>Username</th>
            <th>Rank</th>
            <th>Money</th>
          </tr>
        </thead>
        <tbody>
          {filteredPlayers.map((player, index) => (
            <tr key={player.playerId}>
              <td>{player.country}</td>
              <td>{player.name}</td>
              <td>{index + 1}</td>
              <td>{player.money}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
