import React, { useEffect, useState, useRef } from "react";
import { Toast } from "primereact/toast";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { InputText } from "primereact/inputtext";
import { Panel } from "primereact/panel";

import './App.css';
import './index.css';

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const [search, setSearch] = useState("");
  const toast = useRef(null);

  // Veri çekme
  useEffect(() => {
    fetch("http://localhost:5000/leaderboard")
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched data:", data);
        setPlayers(data);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        toast.current.show({
          severity: "error",
          summary: "Error",
          detail: "An error occurred while fetching data.",
        });
      });
  }, []);

  // Arama işlevi
  const onSearchChange = (e) => {
    setSearch(e.target.value);
  };

  // Arama filtresi
  const filteredPlayers = players.filter((player) =>
    player.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <Panel>
      <div className="header">
        <img src="path/to/logo.png" alt="Panteon logo" />
      </div>

      <div className="leaderboard-header">
        <h1>Leaderboard</h1>
      </div>

      <div className="search-bar">
        <InputText 
          value={search} 
          onChange={onSearchChange} 
          placeholder="Search player..." 
          icon="pi pi-search"
          className="p-inputtext-sm p-d-block"
        />
      </div>

      <div className="table-container">
        <DataTable value={filteredPlayers} paginator rows={10} responsiveLayout="scroll">
          <Column  header="Ranking" body={(rowData, rowIndex) => rowIndex + 1} />
          <Column field="playerId" header="ID"  />
          <Column field="name" header="Player Name" />
          <Column field="country" header="Country" />
          <Column field="money" header="Money" />
        </DataTable>
      </div>

      <Toast ref={toast} />
      </Panel>
    </div>
  );
};

export default Leaderboard;
