import React, { useState } from 'react';
import { Button } from 'primereact/button'; 
import { Toast } from 'primereact/toast'; 
import { ProgressSpinner } from 'primereact/progressspinner'; 

const DistributePrize = () => {
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const toast = React.useRef(null);

  const distributePrizes = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:5000/distributePrizes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(data.message);
        toast.current.show({ severity: 'success', summary: 'Başarı', detail: data.message });
      } else {
        setMessage('Ödüller dağıtılamadı! Lütfen tekrar deneyin.');
        toast.current.show({ severity: 'error', summary: 'Hata', detail: 'Ödüller dağıtılamadı!' });
      }
    } catch (error) {
      setMessage('Error occurred: ' + error.message);
      toast.current.show({ severity: 'error', summary: 'Hata', detail: error.message });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="leaderboard-header">
        <h2>Ödülleri Dağıt</h2>
      </div>
      <Button 
        label="Ödülleri Dağıt" 
        icon="pi pi-gift" 
        onClick={distributePrizes} 
        loading={loading} 
        className="p-button-success p-mt-3"
      />
      <div className="p-mt-4">
        {loading && <ProgressSpinner style={{ width: '50px', height: '50px' }} />}
        {message && <p>{message}</p>}
      </div>
      <Toast ref={toast} />
    </div>
  );
};

export default DistributePrize;
