import React, { useEffect, useState, useRef } from "react";
import { Toast } from "primereact/toast";
import "./App.css";
import "./index.css";

const PlayerAround = () => {
  const [playerId, setPlayerId] = useState(0);
    const [playersAround, setPlayersAround] = useState([]);

    const handlePlayerIdChange = (e) => {
        setPlayerId(Number(e.target.value));
    };

    const fetchPlayerAround = async () => {
        const response = await fetch(`http://localhost:5000/player-around/${playerId}`);
        const data = await response.json();
        setPlayersAround(data);
    };

    return (
        <div>
            <input
                type="number"
                value={playerId}
                onChange={handlePlayerIdChange}
                placeholder="Oyuncu ID"
            />
            <button onClick={fetchPlayerAround}>Get Player Around</button>

            <ul>
                {playersAround.map((player) => (
                    <li key={player.playerId}>
                        {player.rank}. {player.name} ({player.country}) - {player.money}$
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default PlayerAround;
