import React, { useEffect, useState } from "react";
import { Button } from 'primereact/button'; 
import { Toast } from 'primereact/toast'; 
import { ProgressSpinner } from 'primereact/progressspinner'; 


const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

const DistributePrize = () => {
  const [winners, setWinners] = useState([]);
  const [loading, setLoading] = useState(false);

  // üìå Kazananlarƒ± getir
  useEffect(() => {
    fetchWinners();
  }, []);

  const fetchWinners = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/winners`);
      const data = await response.json();
      setWinners(data);
    } catch (error) {
      console.error("‚ùå Error fetching winners:", error);
    }
  };

  // üèÜ √ñd√ºlleri daƒüƒ±t butonu
  const distributePrizes = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/distributePrizes`, { method: "POST" });
      if (!response.ok) throw new Error("Failed to distribute prizes");
      
      alert("üèÜ Prizes distributed successfully!");
      fetchWinners(); // Kazananlarƒ± tekrar √ßek
    } catch (error) {
      console.error("‚ùå Error distributing prizes:", error);
      alert("‚ùå Error distributing prizes.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>üèÜ Admin Panel - Prize Distribution</h1>

      <button 
        onClick={distributePrizes} 
        disabled={loading} 
        className="distribute-button"
      >
        {loading ? "Distributing..." : "Distribute Prizes"}
      </button>

      <h2>Last Winners</h2>
      <table>
        <thead>
          <tr>
            <th>Rank</th>
            <th>Player</th>
            <th>Prize Money</th>
          </tr>
        </thead>
        <tbody>
          {winners.length > 0 ? (
            winners.map((player, index) => (
              <tr key={index}>
                <td>{player.rank}</td>
                <td>{player.name}</td>
                <td>${player.money}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">No winners yet.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default DistributePrize;
