import React, { useState } from "react";
import { Toast } from "primereact/toast";
import { InputNumber } from "primereact/inputnumber";
import { Button } from "primereact/button";
import axios from "axios";
import { InputText } from "primereact/inputtext";
import { Dropdown } from "primereact/dropdown";

const UpdateScore = () => {
  const [playerId, setPlayerId] = useState(null);
  const [money, setMoney] = useState(null);
  const [name, setName] = useState("");
  const [country, setCountry] = useState(null);
  const [score, setScore] = useState(null);

  const [toast, setToast] = useState(null);

  // Ülke seçeneklerini tanımla
  const countries = [
    "Turkey",
    "USA",
    "Brazil",
    "Canada",
    "Germany",
    "France",
    "Italy",
    "UK",
  ];

  const handlePlayerIdChange = (e) => {
    setPlayerId(e.value);
  };

  const handleMoneyChange = (e) => {
    setMoney(e.value);
  };

  const handleScoreChange = (e) => {
    setScore(e.value);
  };

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleCountryChange = (e) => {
    setCountry(e.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!playerId || !score) {
      console.log("Player ID and Country are required!");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/updateScore", {
        playerId,
        // money,
        // name,
        //country,
        score
      });
      console.log("Success: ", response.data);
    } catch (error) {
      console.log("Failed to update score.");
    }
  };

  return (
    <div className="container">
      {/* <Toast ref={(el) => setToast(el)} /> */}
      <div className="leaderboard-header">
        <h2>Kullanıcı Puanını Güncelle</h2>
      </div>

      <div className="form-container">
        <div className="form-field">
          <label htmlFor="playerId" className="form-field">
            Player ID
          </label>
          <InputNumber
            id="playerId"
            value={playerId}
            onValueChange={handlePlayerIdChange}
            showButtons
            min={1}
            max={1000000}
            required
            className="input-field"
          />
        </div>

         <div className="form-field">
          <label htmlFor="score">Skor</label>
          <InputNumber
            id="score"
            value={score}
            onValueChange={handleScoreChange}
            showButtons
            min={0}
            required
            className="input-field"
          />
        </div>

        {/* <div className="form-field">
          <label htmlFor="money">Money</label>
          <InputNumber
            id="money"
            value={money}
            onValueChange={handleMoneyChange}
            showButtons
            min={0}
            required
            className="input-field"
          />
        </div>

        <div className="form-field">
          <label htmlFor="name">Name</label>
          <InputText
            id="name"
            value={name}
            onChange={handleNameChange}
            required
            className="input-field"
          />
        </div>

        <div className="form-field">
          <label htmlFor="country">Country</label>
          <Dropdown
            id="country"
            value={country}
            options={countries}
            onChange={handleCountryChange}
            placeholder="Select a Country"
            required
            className="input-field"
          />
        </div> */}

        <Button
          label="Skoru güncelle"
          icon="pi pi-check"
          onClick={handleSubmit}
          className="submit-button"
        />
      </div>
    </div>
  );
};

export default UpdateScore;
