import React, { useEffect, useState, useRef } from "react";
import { Toast } from "primereact/toast";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { InputText } from "primereact/inputtext";
import { Dropdown } from "primereact/dropdown";
import { AutoComplete } from "primereact/autocomplete"; // âœ… AutoComplete ekledik
import "./App.css";
import "./index.css";

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedCountry, setSelectedCountry] = useState("All");
  const [suggestions, setSuggestions] = useState([]); // âœ… AutoComplete iÃ§in Ã¶neriler
  const toast = useRef(null);

  useEffect(() => {
    fetch("http://localhost:5000/leaderboard")
      .then((res) => res.json())
      .then((data) => {
        setPlayers(data);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        toast.current.show({
          severity: "error",
          summary: "Error",
          detail: "An error occurred while fetching data.",
        });
      });
  }, []);

  const countries = [
    "All",
    ...new Set(players.map((player) => player.country)),
  ];

  // KullanÄ±cÄ± sadece yazdÄ±ÄŸÄ±nda tabloyu gÃ¼ncelleme (Sadece Ã¶nerileri gÃ¶sterir)
  const onSearchChange = (e) => {
    setSearch(e.value);
  };

  // KullanÄ±cÄ± bir oyuncuya tÄ±klarsa, tabloyu sadece o oyuncuya gÃ¶re filtrele
  const onSearchSelect = (e) => {
    setSearch(e.value);
    setSelectedCountry("All"); // Ãœlke filtresini sÄ±fÄ±rla
  };

  const onCountryChange = (e) => {
    setSelectedCountry(e.value);
  };

  // AutoComplete iÃ§in Ã¼lke filtresini de dikkate al
  const onSearchInput = (event) => {
    const query = event.query.toLowerCase();
    const filteredPlayers = players.filter(
      (player) =>
        player.name.toLowerCase().includes(query) &&
        (selectedCountry === "All" || player.country === selectedCountry) // Ãœlke filtresini uygula
    );

    const filteredSuggestions = filteredPlayers.map((player) => player.name);
    setSuggestions(filteredSuggestions);
  };

  const filteredPlayers = players.filter(
    (player) =>
      player.name.toLowerCase().includes(search.toLowerCase()) &&
      (selectedCountry === "All" || player.country === selectedCountry)
  );

  return (
    <div>
      <div className="leaderboard-header">
        <h1>Leaderboard</h1>
      </div>

      <div className="filters">
        {/* ðŸ”¹ 4. Otomatik tamamlama arama kutusu */}
        <AutoComplete
          value={search}
          suggestions={suggestions}
          completeMethod={onSearchInput}
          onChange={onSearchChange} // âœ… KullanÄ±cÄ± yazdÄ±ÄŸÄ±nda tabloyu deÄŸiÅŸtirme
          onSelect={onSearchSelect} // âœ… KullanÄ±cÄ± tÄ±kladÄ±ÄŸÄ±nda tabloyu filtrele
          placeholder="Search player..."
          className="p-autocomplete"
        />

        <Dropdown
          value={selectedCountry}
          options={countries.map((country) => ({
            label: country,
            value: country,
          }))}
          onChange={onCountryChange}
          placeholder="Filter by Country"
          className="p-dropdown-sm"
        />
      </div>

      <div className="table-container">
        <DataTable
          value={filteredPlayers}
          paginator
          rows={10}
          reorderableColumns
          responsiveLayout="scroll"
        >
          <Column field="name" header="Player Name" sortable reorderable />
          <Column field="country" header="Country" sortable reorderable />
          <Column field="money" header="Money" sortable reorderable />
        </DataTable>
      </div>

      <Toast ref={toast} />
    </div>
  );
};

export default Leaderboard;
