import React, { useState, useEffect, useRef } from "react";
import { Button } from 'primereact/button';
import { InputNumber } from "primereact/inputnumber";
import { Dropdown } from "primereact/dropdown";
import { Toast } from "primereact/toast";

const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

const UpdateScore = () => {
  const [players, setPlayers] = useState([]); // Oyuncu listesi
  const [playerId, setPlayerId] = useState(null);
  const [amount, setAmount] = useState(0);
  const toast = useRef(null);

  // ðŸ“Œ Oyuncu Listesini API'den Ã‡ek
  useEffect(() => {
    fetchPlayers();
  }, []);

  const fetchPlayers = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/leaderboard`);
      const data = await response.json();
      setPlayers(data.map(p => ({ label: p.name, value: p.id }))); // Dropdown iÃ§in uygun format
    } catch (error) {
      console.error("âŒ Error fetching players:", error);
    }
  };

  // ðŸŽ¯ Skor GÃ¼ncelleme Fonksiyonu
  const updateScore = async () => {
    if (!playerId || amount <= 0) {
      toast.current.show({ severity: "warn", summary: "Warning", detail: "Please enter valid player and amount!", life: 3000 });
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/reset/updateScore`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ playerId, amount }),
      });

      if (!response.ok) throw new Error("Failed to update score");

      toast.current.show({ severity: "success", summary: "Success", detail: "Score updated successfully!", life: 3000 });

      fetchPlayers(); // GÃ¼ncellenmiÅŸ oyuncu listesini Ã§ek
    } catch (error) {
      console.error("âŒ Error updating score:", error);
      toast.current.show({ severity: "error", summary: "Error", detail: "âŒ Failed to update score.", life: 3000 });
    }
  };

  return (
    <div className="container">
      <h1>ðŸŽ¯ Update Score</h1>

      <Toast ref={toast} />

      {/* ðŸ”¹ Oyuncu SeÃ§imi */}
      <Dropdown
        value={playerId}
        options={players} // API'den Ã§ekilen oyuncu listesi
        onChange={(e) => setPlayerId(e.value)}
        placeholder="Select a player"
        className="p-dropdown"
      />

      {/* ðŸ”¹ Skor ArtÄ±rma */}
      <InputNumber
        value={amount}
        onValueChange={(e) => setAmount(e.value)}
        mode="decimal"
        placeholder="Enter amount"
        className="p-inputnumber"
      />

      <Button label="Update Score" icon="pi pi-plus" className="p-button-success" onClick={updateScore} />
    </div>
  );
};

export default UpdateScore;
