import React, { useEffect, useState, useRef } from "react";
import { Toast } from "primereact/toast";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { AutoComplete } from "primereact/autocomplete";
import { Dropdown } from "primereact/dropdown";
import "./App.css";
import "./index.css";
import "./flag.css";

const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000";

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedCountry, setSelectedCountry] = useState("All");
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const toast = useRef(null);

  // ðŸŒŸ SÃ¼tunlarÄ± useRef ile yÃ¶net
  const columnsRef = useRef([
    { field: "rank", header: "Ranking" },
    { field: "name", header: "Player Name" },
    { field: "country", header: "Country" },
    { field: "money", header: "Money" },
  ]);

  // ðŸ“Œ API'den veriyi Ã§ek
  useEffect(() => {
    const fetchLeaderboard = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/leaderboard`);
        if (!response.ok) throw new Error("Failed to fetch leaderboard");

        let data = await response.json();

        console.log(data);


        // ðŸ“Œ Ranking (sÄ±ralama) hesapla
        data = data
          .sort((a, b) => b.money - a.money) // BÃ¼yÃ¼kten kÃ¼Ã§Ã¼ÄŸe sÄ±rala
          .map((player, index) => ({
            ...player,
            rank: index + 1,
            name: player.name || "Unknown Player", // ðŸ”¹ EÄŸer NULL ise 'Unknown Player'
          }));

        setPlayers(data);
        setFilteredPlayers(data); // TÃ¼m oyuncular baÅŸlangÄ±Ã§ta gÃ¶sterilecek
      } catch (error) {
        console.error("Fetch error:", error);
        if (toast.current) {
          toast.current.show({
            severity: "error",
            summary: "Error",
            detail: "An error occurred while fetching data.",
          });
        }
      }
    };

    fetchLeaderboard();
  }, []);

  // ðŸ“Œ Search ve Ãœlkeye GÃ¶re Filtreleme
  useEffect(() => {
    let filtered = [...players];

    

    if (search.trim() !== "") {
      filtered = filtered.filter(player =>
        player.name.toLowerCase().includes(search.toLowerCase()) // ðŸ”¹ username yerine name kullanÄ±ldÄ±
      );
    }

    if (selectedCountry !== "All") {
      filtered = filtered.filter(player => player.country === selectedCountry);
    }

    

    setFilteredPlayers(filtered);
  }, [search, selectedCountry, players]);

  // ðŸ“Œ Search Ã¶nerilerini oluÅŸtur
  const onSearchInput = (event) => {
    const query = event.query.toLowerCase();
    const filtered = players.filter(player =>
      player.name.toLowerCase().includes(query) // ðŸ”¹ username yerine name kullanÄ±ldÄ±
    );
    setSuggestions(filtered.map(player => player.name)); // ðŸ”¹ username yerine name kullanÄ±ldÄ±
  };

  // ðŸ“Œ Ãœlke BayraklarÄ±nÄ± GÃ¶sterme
  const countryBodyTemplate = (rowData) => {
    const countryName = rowData?.country || "Unknown"; // EÄŸer country yoksa "Unknown"
    const countryCode = rowData?.country_code?.toLowerCase(); // KÃ¼Ã§Ã¼k harfe Ã§evir
  
    return (
      <div className="flex align-items-center gap-2">
        {countryCode ? (
          <img
            alt={countryName}
            src={`https://flagcdn.com/w40/${countryCode}.png`} // Bayrak URL'sini kÃ¼Ã§Ã¼k harfe Ã§evirerek kullan
            className="flag"
            style={{ width: "24px", height: "16px" }}
            onError={(e) => (e.target.style.display = "none")} // Bayrak yÃ¼klenmezse gizle
          />
        ) : (
          <span style={{ fontStyle: "italic", color: "gray" }}>No Flag</span>
        )}
        <span>{countryName}</span>
      </div>
    );
  };
  
  // ðŸ“Œ Ãœlke seÃ§enekleri
  const countryOptions = ["All", ...new Set(players.map(player => player.country))];

  return (
    <div className="container">
      <div className="leaderboard-header">
        <h1>Leaderboard</h1>
      </div>

      <div className="filters">
        {/* ðŸ”¹ Search (Arama) */}
        <AutoComplete
          value={search}
          suggestions={suggestions}
          completeMethod={onSearchInput}
          onChange={(e) => setSearch(e.value)}
          placeholder="Search player..."
          className="p-autocomplete"
        />

        {/* ðŸ”¹ Ãœlkeye GÃ¶re Filtreleme */}
        <Dropdown
          value={selectedCountry}
          options={countryOptions.map(country => ({ label: country, value: country }))}
          onChange={(e) => setSelectedCountry(e.value)}
          placeholder="Filter by Country"
          className="p-dropdown"
        />
      </div>

      {/* ðŸŒŸ DataTable */}
      <div className="table-container">
        <DataTable
          value={filteredPlayers}
          paginator
          rows={10}
          reorderableColumns
          responsiveLayout="scroll"
        >
          {columnsRef.current.map((col, index) => (
            <Column
              key={index}
              field={col.field}
              header={col.header}
              sortable
              reorderable
              body={col.field === "country" ? countryBodyTemplate : null}
            />
          ))}
        </DataTable>
      </div>

      <Toast ref={toast} />
    </div>
  );
};

export default Leaderboard;
