import React, { useState } from 'react';
import { Toast } from 'primereact/toast';
import axios from 'axios';

const UpdateScore = () => {
  const [playerId, setPlayerId] = useState('');
  const [money, setMoney] = useState('');
  const [toast, setToast] = useState(null);

  const handlePlayerIdChange = (e) => {
    setPlayerId(e.target.value);
  };

  const handleMoneyChange = (e) => {
    setMoney(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!playerId || !money) {
      setToast({
        severity: 'error',
        summary: 'Error',
        detail: 'Player ID and Money are required!',
      });
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/updateScore', {
        playerId,
        money,
      });
      setToast({
        severity: 'success',
        summary: 'Success',
        detail: response.data,
      });
    } catch (error) {
      setToast({
        severity: 'error',
        summary: 'Error',
        detail: 'Failed to update score.',
      });
    }
  };

  return (
    <div>
      <Toast ref={(el) => setToast(el)} />
      <h2>Update Player Score</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Player ID: </label>
          <input
            type="number"
            value={playerId}
            onChange={handlePlayerIdChange}
            required
          />
        </div>
        <div>
          <label>Money: </label>
          <input
            type="number"
            value={money}
            onChange={handleMoneyChange}
            required
          />
        </div>
        <button type="submit">Update Score</button>
      </form>
    </div>
  );
};

export default UpdateScore;
