import React, { useEffect, useState, useRef } from "react";
import { Toast } from "primereact/toast";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { InputText } from "primereact/inputtext";
import { Dropdown } from "primereact/dropdown";
import "./App.css";
import "./index.css";

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedCountry, setSelectedCountry] = useState("All");
  const toast = useRef(null);

  useEffect(() => {
    fetch("http://localhost:5000/leaderboard")
      .then((res) => res.json())
      .then((data) => {
        setPlayers(data);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        toast.current.show({
          severity: "error",
          summary: "Error",
          detail: "An error occurred while fetching data.",
        });
      });
  }, []);

  const countries = [
    "All",
    ...new Set(players.map((player) => player.country)),
  ];

  const onSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const onCountryChange = (e) => {
    setSelectedCountry(e.value);
  };

  const filteredPlayers = players.filter(
    (player) =>
      player.name.toLowerCase().includes(search.toLowerCase()) &&
      (selectedCountry === "All" || player.country === selectedCountry)
  );

  return (
    <div>
      <div className="leaderboard-header">
        <h1>Leaderboard</h1>
      </div>

      <div className="filters">
        <InputText
          value={search}
          onChange={onSearchChange}
          placeholder="Search player..."
          className="p-inputtext-sm p-d-block"
        />

        <Dropdown
          value={selectedCountry}
          options={countries.map((country) => ({
            label: country,
            value: country,
          }))}
          onChange={onCountryChange}
          placeholder="Filter by Country"
          className="p-dropdown-sm"
        />
      </div>

      {/* ðŸš€ PrimeReact DataTable ile sÃ¼rÃ¼kleyerek sÄ±ralama */}
      <div className="table-container">
        <DataTable
          value={filteredPlayers}
          paginator
          rows={10}
          reorderableColumns
          responsiveLayout="scroll"
          onColReorder={(e) => setPlayers([...players])} // SÃ¼tunlarÄ± yeniden dÃ¼zenleme
        >
          <Column field="name" header="Player Name" sortable reorderable />
          <Column field="country" header="Country" sortable reorderable />
          <Column field="money" header="Money" sortable reorderable />
        </DataTable>
      </div>

      <Toast ref={toast} />
    </div>
  );
};

export default Leaderboard;
