import React, { useState } from 'react';
import { Toast } from 'primereact/toast';
import { InputNumber } from 'primereact/inputnumber';
import { Button } from 'primereact/button';
import axios from 'axios';

const UpdateScore = () => {
  const [playerId, setPlayerId] = useState(null);
  const [money, setMoney] = useState(null);
  const [name, setName] = useState(null);
  const [country, setCountry] = useState(null);

  const [toast, setToast] = useState(null);

  // Toast durumunu sadece gerekli olduğunda güncelle
//   const showToast = (severity, summary, detail) => {
//     setToast({
//       severity,
//       summary,
//       detail,
//     });
//   };

  const handlePlayerIdChange = (e) => {
    setPlayerId(e.value);
  };

  const handleMoneyChange = (e) => {
    setMoney(e.value);
  };

  
  const handleNameChange = (e) => {
    setName(e.value);
  };

  
  const handleCountryChange = (e) => {
    setCountry(e.value);
  };


  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!playerId || !money) {
      //showToast('error', 'Error', 'Player ID and Money are required!');
      console.log("Player ID and Money are required!");
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/updateScore', {
        playerId,
        money,
      });
      //showToast('success', 'Success', response.data);
      console.log("Success: ", response.data);

    } catch (error) {
      //showToast('error', 'Error', 'Failed to update score.');
      console.log("Failed to update score.");

    }
  };

  return (
    <div className="p-fluid p-formgrid p-grid">
      {/* <Toast ref={(el) => setToast(el)} /> */}
      <h2 className="p-col-12">Update Player Score</h2>
      
      <div className="p-field p-col-12 p-md-6">
        <label htmlFor="playerId">Player ID</label>
        <InputNumber
          id="playerId"
          value={playerId}
          onValueChange={handlePlayerIdChange}
          showButtons
          min={1}
          max={1000000}
          required
          className="p-inputtext-sm"
        />
      </div>

      <div className="p-field p-col-12 p-md-6">
        <label htmlFor="money">Money</label>
        <InputNumber
          id="money"
          value={money}
          onValueChange={handleMoneyChange}
          showButtons
          min={0}
          required
          className="p-inputtext-sm"
        />
      </div>

      <div className="p-field p-col-12 p-md-6">
        <label htmlFor="name">Name</label>
        <InputNumber
          id="name"
          value={name}
          onValueChange={handleNameChange}
          showButtons
          min={0}
          required
          className="p-inputtext-sm"
        />
      </div>

      <div className="p-field p-col-12 p-md-6">
        <label htmlFor="country">Country</label>
        <InputNumber
          id="country"
          value={country}
          onValueChange={handleCountryChange}
          showButtons
          min={0}
          required
          className="p-inputtext-sm"
        />
      </div>

      <div className="p-col-12">
        <Button label="Update Score" icon="pi pi-check" onClick={handleSubmit} />
      </div>
    </div>
  );
};

export default UpdateScore;
