import React, { useEffect, useState, useRef } from "react";
import { Toast } from "primereact/toast";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { InputText } from "primereact/inputtext";
import { Dropdown } from "primereact/dropdown";
import './App.css';
import './index.css';

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const [search, setSearch] = useState("");
  const [selectedCountry, setSelectedCountry] = useState("All");
  const toast = useRef(null);

  // Veri çekme
  useEffect(() => {
    fetch("http://localhost:5000/leaderboard")
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched data:", data);
        setPlayers(data);
      })
      .catch((err) => {
        console.error("Fetch error:", err);
        toast.current.show({
          severity: "error",
          summary: "Error",
          detail: "An error occurred while fetching data.",
        });
      });
  }, []);

  // Ülke listesi oluşturma
  const countries = ["All", ...new Set(players.map((player) => player.country))];

  // Arama işlevi
  const onSearchChange = (e) => {
    setSearch(e.target.value);
  };

  // Ülke seçimi değiştiğinde filtreleme
  const onCountryChange = (e) => {
    setSelectedCountry(e.value);
  };

  // Arama ve ülkeye göre filtreleme
  const filteredPlayers = players.filter(
    (player) =>
      player.name.toLowerCase().includes(search.toLowerCase()) &&
      (selectedCountry === "All" || player.country === selectedCountry)
  );

  return (
    <div>
      <div className="header">
        <img src="path/to/logo.png" alt="Panteon logo" />
      </div>

      <div className="leaderboard-header">
        <h1>Leaderboard</h1>
      </div>

      {/* Arama ve Ülke Filtreleme Alanı */}
      <div className="filters">
        <InputText 
          value={search} 
          onChange={onSearchChange} 
          placeholder="Search player..." 
          className="p-inputtext-sm p-d-block"
        />

        <Dropdown
          value={selectedCountry}
          options={countries.map((country) => ({ label: country, value: country }))}
          onChange={onCountryChange}
          placeholder="Filter by Country"
          className="p-dropdown-sm"
        />
      </div>

      {/* Liderlik Tablosu */}
      <div className="table-container">
        <DataTable value={filteredPlayers} paginator rows={10} responsiveLayout="scroll">
          <Column header="Ranking" body={(rowData, rowIndex) => rowIndex + 1} />
          <Column field="playerId" header="ID" />
          <Column field="name" header="Player Name" />
          <Column field="country" header="Country" />
          <Column field="money" header="Money" />
        </DataTable>
      </div>

      <Toast ref={toast} />
    </div>
  );
};

export default Leaderboard;
